// If you use this as a template, replace IBM Corp. with your own name.

// Sample Node-RED node file

// Require main module
var RED = require(process.env.NODE_RED_HOME+"/red/red");
var request = require('request');


function sendSMS(message, from, to, account, username, password){
	
	var msg = "<?xml version='1.0' encoding='UTF-8'?><messages> <accountreference>"+account+"</accountreference> <message><from>"+from+"</from><to>"+to+"</to><type>SMS</type><body>"+message+"</body> </message></messages>";
	
	auth = "Basic " + new Buffer(username + ":" + password).toString("base64");
	
	request.post({
		headers : {
			'content-type' : 'application/x-www-form-urlencoded',
			"Authorization" : auth
		},
		url : 'https://api.esendex.com/v1.0/messagedispatcher',
		body : msg
	}, function(error, response, body) {
		console.log(body);
	});
}

function EsendexNode(n) {

	RED.nodes.createNode(this,n);


    // send out the message to the rest of the workspace.
 //   this.send(msg);

    this.on("input", function(msg) {
    	
        var message = msg.payload;
        var from = msg.from||n.from;
        var to = msg.to||n.to;
        var account = msg.account||n.account;
        var username = msg.username||n.username;
        var password = msg.password||n.password;
    	
    	sendSMS(message, from, to, account, username, password);
    });
}

// Register the node by name. This must be called before overriding any of the
// Node functions.
RED.nodes.registerType("esendex",EsendexNode);
